from pathlib import Path
from tkinter import Tk, Canvas, Button, Label, PhotoImage
from PIL import Image, ImageTk

# Paths
OUTPUT_PATH = Path(__file__).parent
ASSETS_PATH = OUTPUT_PATH / Path(r"E:\1_MINIPROJECT\build\assets\frame1")

def relative_to_assets(path: str) -> Path:
    return ASSETS_PATH / Path(path)

# Window Setup
window = Tk()
window.geometry("1440x1024")
window.configure(bg="#F5E8C7")
window.title("Coffee Shop")

canvas = Canvas(
    window,
    bg="#F5E8C7",
    height=1024,
    width=1440,
    bd=0,
    highlightthickness=0,
    relief="ridge"
)
canvas.place(x=0, y=0)

# Navigation Bar
canvas.create_rectangle(0, 3, 1440, 83, fill="#5B3A29", outline="")
nav_buttons = ["Home", "Orders", "Menu", "Logout"]
button_x = [50, 200, 350, 1250]
for i, text in enumerate(nav_buttons):
    Button(
        window, text=text, font=("Montserrat", 14, "bold"), bg="#EAD7A2", fg="#000",
        relief="flat", width=10, height=1
    ).place(x=button_x[i], y=20)

# Handcrafted Curations
canvas.create_text(50, 100, text="Handcrafted Curations", font=("Montserrat", 20, "bold"), fill="#000")

curation_images = ["bestseller.png", "drinks.png", "food.png"]
curation_labels = ["Bestseller", "Drinks", "Food"]
curation_x = [100, 400, 700]
curation_y = 140

for i in range(3):
    img = Image.open(relative_to_assets(curation_images[i])).resize((150, 150))
    img = ImageTk.PhotoImage(img)
    label = Label(window, image=img, bg="#F5E8C7")
    label.image = img  # Keep reference
    label.place(x=curation_x[i], y=curation_y)
    
    Label(window, text=curation_labels[i], font=("Montserrat", 14, "bold"), bg="#F5E8C7").place(x=curation_x[i] + 35, y=300)

# Reviews Section
canvas.create_text(50, 350, text="Reviews", font=("Montserrat", 20, "bold"), fill="#000")
reviews = [
    ("JD", "John D", "Amazing! Exceeded expectations.", 5, "Feb 18, 2025"),
    ("JS", "Jordan S", "Exceeded my expectations", 4, "Feb 21, 2025"),
    ("EE", "Emily E", "okay, but not great.", 3, "Feb 28, 2025")
]
review_x = [100, 400, 700]
review_y = 390

for i, (initials, name, comment, stars, date) in enumerate(reviews):
    Label(window, text=initials, font=("Montserrat", 16, "bold"), bg="#EAD7A2", width=4, height=2).place(x=review_x[i], y=review_y)
    Label(window, text=name, font=("Montserrat", 14, "bold"), bg="#F5E8C7").place(x=review_x[i] + 50, y=review_y)
    Label(window, text="★" * stars + "☆" * (5 - stars), font=("Montserrat", 14), bg="#F5E8C7", fg="#FFD700").place(x=review_x[i] + 50, y=review_y + 30)
    Label(window, text=f'"{comment}"', font=("Montserrat", 12, "italic"), bg="#F5E8C7").place(x=review_x[i] + 50, y=review_y + 60)
    Label(window, text=date, font=("Montserrat", 10), bg="#F5E8C7").place(x=review_x[i] + 50, y=review_y + 90)

# Footer
canvas.create_rectangle(0, 712, 1440, 1024, fill="#5B3A29", outline="")
canvas.create_text(100, 770, text="| Letters & Lattes | Privacy Policy | Terms | Contact Us ", font=("Montserrat", 14, "bold"), fill="#FFFFFF")

# Social Media Icons
social_icons = ["instagram.png", "whatsapp.png", "x.png"]
social_x = [100, 150, 200]
social_y = 850

for i in range(3):
    img = Image.open(relative_to_assets(social_icons[i])).resize((40, 40))
    img = ImageTk.PhotoImage(img)
    label = Label(window, image=img, bg="#5B3A29")
    label.image = img  # Keep reference
    label.place(x=social_x[i], y=social_y)

window.resizable(False, False)
window.mainloop()
